rules.default:
{
    "ruleSearch": {
        "author": "p@text##.*</a>",
        "bookList": ".cover p.line",
        "bookUrl": "a@href",
        "name": "a@text"
    },
    "ruleToc": {
        "chapterList": ".chapter li",
        "chapterName": "a@text",
        "chapterUrl": "a@href"
    },
    "ruleContent": {
        "content": "id.nr1@html",
        "nextContentUrl": "id.pt_next@href",
        "replaceRegex": "(?:第[一二三四五六七八九十0-9]+章.*?\\(第\\d+/\\d+页\\))|(?:\\(本章未完，请点击下一页继续阅读\\))|(?:最新网址：3g\\.shugelou\\.org)|(?:每完成一次任务，系统就会给予宿主相应的奖励。)|(?:【.*?】)|(?:\\s*\\(第\\d+/\\d+页\\))",
        "title": "id._bqgmb_h1@text"
    },
    "ruleFind": {
        "findList": ".content li",
        "findName": "a@text",
        "findUrl": "a@href"
    }
}
